insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('1cfc1f8ff2dc4049b2422177cd3b9040',' 美国国家综合癌症网络(NCCN,National Comprehensive Cancer Network)',NULL,'1.吸烟史\n2.氡暴露史\n3.职业暴露：砒霜、铬、石棉、镍、镉、铍、硅、柴油烟雾（吸烟可以叠加）\n4.一级亲属罹患肺癌\n5.COPD及肺纤维化病史\n6.报警症状','年龄大于等于55岁，并且吸烟指数大于30支.年，戒烟时间小于15年\r\n或年龄大于50岁，并且吸烟指数大于20支.年，或包含任意一条危险因素（见关注因素）',NULL,'普通人群不推荐筛查',NULL,2,NULL,NULL,'d512d601abf64077ba881afb6b0de817','NCCN临床实践指南：肺癌筛查（2016.V2）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('2de4864de9eb4e1a9b4549bd75d39316',' 美国预防医学工作组(USPSTF,U.S. Preventive Services Task Force)',NULL,'TSH 升高的风险包括：女性、年老、白种人、1 型糖尿病、唐氏综合症、甲状腺疾病家族史、甲状腺肿大、曾患甲亢（部分是消融疗法导致的甲状腺功能障碍），头颈部的体外放射治疗。\r\nTSH 降低风险包括：女性、年老、黑种人、低碘摄入、甲状腺疾病的家族及个人史、服用含碘药物如胺碘酮。','',NULL,'',NULL,1,NULL,NULL,NULL,'2015 USPSTF建议声明：甲状腺功能不全的筛查');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('365153d2e9e44bf0a15ec2fb25c97c31',' 美国预防医学工作组(USPSTF,U.S. Preventive Services Task Force)',NULL,'1.性别\r\n2.年龄\r\n3.吸烟\r\n4.家族史（一级亲属罹患腹主动脉瘤）\r\n5.既往史（既往被诊断：血管瘤、心、脑血管疾病、动脉粥样硬化、高胆固醇血症、肥胖和高血压）','男性\r\n吸烟超过100只\r\n年龄大于65岁',NULL,'女性、包含以上高危因素',NULL,1,NULL,NULL,'3e8727d3ca9349a58b4990edaa0db903','2014 USPSTF 腹主动脉瘤筛查推荐声明');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('3841703bd931482fb3bc425b4178b813',' 中华医学会内镜学分会(CSDE,Chinese Society of Digestive Endoscopy) 中华医学会消化病学分会(CSGE,Chinese Society Of Gastroenterology)',NULL,'1.年龄\r\n2.结直肠腺瘤、锯齿状息肉及结直肠癌病史\r\n3.非特异性肠炎病史\r\n4.阳性家族史（Lynch综合征病史、多发性错构瘤综合征病史等）\r\n5.大便隐血实验结果\r\n6.具有以下两项及两项以上者：①慢性便秘；②黏液血便；③慢性腹泻；④慢性阑尾炎或阑尾切除史；⑤精神刺激史；⑥体重指数 (BMI) ＞28 kg/m2；⑦吸烟指数大于400只/年；慢性胆囊炎及胆囊切除史；慢性便秘指近2年来便秘每年在2个月以上','1.有便血、黑便、体重减轻、贫血等报警症状的人群\r\n2.大于50岁，包含：结直肠腺瘤、锯齿状息肉及结直肠癌病史；非特异性肠炎病史；阳性家族史（Lynch综合征病史、多发性错构瘤综合征病史等）可以理解为一级亲属有结直肠癌病史；大便隐血实验结果阳性；具有以下两项及两项以上者：①慢性便秘；②黏液血便；③慢性腹泻；④慢性阑尾炎或阑尾切除史；⑤精神刺激史；⑥体重指数 (BMI) ＞28 kg/m2；⑦吸烟指数大于400只/年；慢性胆囊炎及胆囊切除史；慢性便秘指近2年来便秘每年在2个月以上',NULL,'年龄大于等于50岁，无以上危险因素',NULL,2,NULL,NULL,'145909f7189e4aabade2564902f76f2c','2015 中国早期结直肠癌及癌前病变筛查与诊治共识');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('40c2f59a7b2c4b50937da37b2b9f7919',' 中华医学会心血管病学分会(Chinese Society of Cardiology)',NULL,'既往史：先天性心脏病、结缔组织病、HIV感染、减肥药物治疗史、肝病及贫血\r\n个人史：职业解除如印刷厂或加油站工人接触油类物品、HIV感染、男-男同性恋、吸毒及染发剂接触	婚育史：女性是否有习惯性流产、男性需了解直系女性亲属是否有习惯性流产	\r\n家族史：是否有诊断肺动脉高压或先心病史\r\n','既往史：先天性心脏病、结缔组织病、HIV感染、减肥药物治疗史、肝病及贫血\r\n个人史：职业解除如印刷厂或加油站工人接触油类物品、HIV感染、男-男同性恋、吸毒及染发剂接触	婚育史：女性是否有习惯性流产、男性需了解直系女性亲属是否有习惯性流产	\r\n家族史：是否有诊断肺动脉高压或先心病史\r\n胸部影像学检测提示有右心脏增大及肺动脉增粗表现\r\n如有非特异性临床表现和体征可更考虑疑诊',NULL,'',NULL,1,NULL,NULL,'67b14f7fb6164db38b34f9cecbc78a9e','2007 中国肺动脉高压筛查诊断与治疗专家共识');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('56f55df13386472c98df5a082a3ec9ae',' 美国预防医学工作组(USPSTF,U.S. Preventive Services Task Force)',NULL,'1.血压在正常高值者（130-139/85-89 mmHg）\r\n2.BMI大于24\r\n3.腹型肥胖：男性腰围大于90cm，女性大于85cm\r\n4.家族史（一、二级亲属罹患高血压）\r\n5.长期高盐饮食\r\n6.长期饮酒，日均大于等于100ml（2两）\r\n来源于（中国高血压基层管理指南（2014年修订版））','≥40岁的成人，血压在正常高值者（130-139/85-89 mmHg）、超重或肥胖者。',NULL,'18-39岁、血压正常（<130/85 mmHg）且没有危险因素。',NULL,1,NULL,NULL,'6e3256bed9694c11930bc5251ef42678','2015 USPSTF建议声明：成人高血压筛查');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('68db0e163064409286056fe21f7f27ad',' 北京市脑卒中诊疗质量控制与改进中心',NULL,'1.脂代谢异常\r\n2.高血压、糖尿\r\n3.吸烟及吸烟环境\r\n4.家族史：一二级亲属罹患脑动脉粥样硬化\r\n5.肥胖\r\n6.高同型半胱氨酸\r\n','大于45岁，包含一下一项或以上：1.高胆固醇或高甘油三酯血症；2.高血压；3.糖尿病；4.吸烟；5.家族史；6.大量饮酒；7.肥胖及不良饮食习惯（高热量、高脂肪）；8.缺乏规律有氧运动；9.被诊断高同型半胱氨酸',NULL,'',NULL,1,NULL,NULL,'750ea039c8b24154b0017c9ba46ce0f5','脑动脉粥样硬化筛查与诊断规范（2014版 中国）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('7c77c03835cc41dda72c27028ecb7585',' 中华医学会外科学分会胆道外科学组',NULL,'1.是否患有胆囊结石2.是否有胆囊慢性炎症3.是否有胆囊息肉4.是否存在胆胰管汇合异常5.家族史：胆囊癌及胆囊结石家族史6.是否有胆道系统感染7.是否肥胖，是否有糖尿病8.','为了预防胆囊癌的发生，出现下列危险因素时应考虑行胆囊切除术，且胆囊标本应广泛取材进行病理学检查：(1)直径>3 cm的胆囊结石；(2)合并有胆囊壁不均匀钙化、点状钙化或多个细小钙化的胆囊炎以及瓷性胆囊；(3)胆囊息肉直径≥10 mm；胆囊息肉直径<10 mm合并胆囊结石、胆囊炎；单发或无蒂的息肉且迅速增大者(增长速度>3 mm/6个月)；(4)合并胆囊结石、胆囊炎的胆囊腺肌症；(5)胰胆管汇合异常合并胆囊占位性病变；(6)胆囊结石合并糖尿病。(推荐等级A级，证据等级Ⅰ级)\r\n推荐2：出现下列情况时，建议间隔6～12个月行彩色多普勒超声动态检查胆囊：(1)胆囊息肉；(2)年龄超过50岁，特别是女性；(3)肥胖症；(4)有胆石症或胆囊癌家族史。(推荐等级A级，证据等级Ⅰ级)',NULL,'',NULL,1,NULL,NULL,'4927a9a1f70f4a4697acd2b62cf65971','胆囊癌诊断和治疗指南(2015版)');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('8a2b68cc6b054aa7a40b5763f14cec28','美国妇产科医师学会(ACOG,American College of Obstetricians and Gynecologists)',NULL,'1.人乳头瘤病毒感染；2.过早性生活；3.多个性伙伴；4.多产；5.丈夫婚外性行为；6.阴茎癌；7.社会经济条件差；8.营养不良；9.吸烟','1.大于等于21岁\r\n2.有性行为',NULL,'无',NULL,1,NULL,NULL,'96c7f45f8f6c44b9aa9047335f385b02','ACOG实践指南157号：宫颈癌的筛查和预防');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('9a1bac66a1b640c2b1e106ecd37797e9',' 美国国家综合癌症网络(NCCN,National Comprehensive Cancer Network)',NULL,'1.年龄\n2.是否有肝硬化\n3.饮酒\n4.乙型肝炎病毒感染\n5.丙型肝炎病毒感染\n6.非酒精性脂肪肝\n7.α1-抗胰蛋白酶缺乏症病史\n8.四期原发性胆汁性肝硬化病史\n9.遗传性血色病病史','1.各种原因导致的肝硬化患者\r\n2.乙肝携带者，男性大于等于40岁，女性大于等于50岁',NULL,'普通人群不推荐',NULL,18,NULL,NULL,'6c8ce194bbd1426da1170781819a890b','NCCN临床实践指南：肝癌（2016.V1）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('a1ccc6a220a4465f964195c539c1d365','中国癌症筛查及早诊早治技术方案 人民卫生出版社',NULL,'1.地区：高发地区：鼻咽癌死亡率＞3/10万的地区（中国标化）：广东、广西、福建、湖南、江西\r\n2.家族史：有家族聚集倾向，一级亲属罹患过鼻咽癌的；\r\n3.喜食咸鱼、腌制食物的、长期接触甲醛的\r\n4.高镍环境（存疑）','满足2和或3-4 条\r\n年龄大于等于30岁',NULL,'',NULL,1,NULL,NULL,'361a207e57464933a70c447376f4b8f2','鼻咽癌筛查及早诊早治指南');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('a26efd48e9484b22b8bf7477f7656630','美国国家综合癌症网络(NCCN,National Comprehensive Cancer Network)',NULL,'1.年龄\r\n2.家族史（一级亲属罹患前列腺癌）','年龄大于等于45岁',NULL,'',NULL,1,NULL,NULL,'e301591d7cb24381b364fb9f43ad864e','NCCN临床实践指南：前列腺癌早期检测（2016.V1）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('a5c0a4318fb547c7bcf015480b2ca8c4',' 中国抗癌协会乳腺癌专业委员会(CACA-CBCS,Chinese Anti-Cancer Association, Committee of Breast Cancer Society)',NULL,'确定性因素：1.年龄；2.性别；3.绝经后体重增加 BMI≥30kg/m2或增加10公斤；4.高身材，身高大于175cm；5.雌激素水平（高内源性雌激素水平）；6.骨密度（升高者发病率升高）；7.良性乳腺疾病史；8.长期使用外源性雌激素；9.初潮较早或绝经较晚；10.未产妇及首次初产年龄大于等于35岁；11.乳腺癌个人史；12.一级亲属罹患乳腺癌；13.饮酒；14.吸烟；15.夜班工作；16.暴露于治疗性电离辐射；17.是否哺乳；18.大豆/植物雌激素应用；19.体育锻炼；\n不确定性因素：未录入','1.有明显的乳腺癌遗传倾向者\r\n2.既往有乳腺良恶性疾病患者\r\n3.既往行胸部放疗',NULL,'无',NULL,1,NULL,NULL,'be36269e1960417a8fe60ed93fd465d3','中国抗癌协会乳腺癌诊治指南与规范（2015版）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('aa5f7b61e93f4c0a8639e2b2d8b3a933','中华医学会外科学分会胰腺外科学组',NULL,'1.年龄；2.家族史；3.60岁以上突发糖尿病；4.慢性胰腺炎病史；5.吸烟；6.饮酒；7.肥胖；8.远端胃大部切除术病史（时间大于20年）；9.接触萘胺及苯类化合物者\r\n前驱症状：消化不良、恶性、脂肪泻、黄疸、疼痛、体重减轻','1.年龄大于40岁，有非特异性上腹部不适症状；\r\n2.一级亲属有罹患胰腺癌\r\n3.年龄大于60岁，突发糖尿病，缺乏家族史，低体重指数，很快形成胰岛素抵抗\r\n4.慢性胰腺炎病史：特别是遗传性胰腺炎病史\r\n5.良性病变行远端胃大部切除术者，特别是术后20年以上人群\r\n6.导管内乳头状粘液瘤患者\r\n7.FAP 家族性腺瘤息肉病患者\r\n8.长期吸烟\r\n9.大量饮酒\r\n10.体重超重\r\n11.黑斑息肉综合征病史\r\n12.家族性恶性黑色素瘤病史',NULL,'',NULL,1,NULL,NULL,'c2aef940bbc14bb598c88223d83c614b','胰腺癌诊治指南（2014）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('ae6bcb0679ba4b7f83ec6aca09757dd6',' 中华人民共和国国家卫生和计划生育委员会(NHFPC,National Health and Family Planning Commission of China)',NULL,'1.高血压史（≥140/90 mmHg）或正在服用降压药物 \r\n2.房颤和（或）心脏瓣膜病等心脏病\r\n3.吸烟\r\n4.血脂异常\r\n5.糖尿病\r\n6.BMI大于26\r\n7.很少进行体育活动\r\n8.有卒中家族史','1.以上8条满足3条\r\n2.既往有缺血性脑卒中病史或短暂性脑缺血发作病史',NULL,'',NULL,1,NULL,NULL,'66e8a724efea4d1d8d54c900ffd1984b','2014 卒中筛查与防治技术规范');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('b324470469f449a883059bebbc195bea',' 中华医学会内镜学分会(CSDE,Chinese Society of Digestive Endoscopy) 中国抗癌协会(Chinese Anti-cancer Association) 中国抗癌协会肿瘤内镜专业委员会',NULL,'1.性别：\r\n2.年龄\r\n3.饮食：加盐腌制蔬菜或烟熏肉和鱼。\r\n4.吸烟\r\n5.治疗：接受过胃部手术。\r\n6.癌前疾病：慢性萎缩性胃炎、胃溃疡、胃息肉。\r\n7.家族肿瘤疾病史：包括家族胃癌史，遗传性非息肉性肠癌，家族性腺瘤性息肉等遗传性疾病等。\r\n8.恶性贫血。\r\n9.肥胖：超过正常体重20～25公斤\r\n10.经济状况：经济较差的地区，胃癌的发病率较高。\r\n11.是否有贫血、消瘦、消化道出血、呕吐、上腹部不适、上腹部肿块.来源:2013卫计委胃癌规范化诊疗指南（试行）',' 男女不限，年龄大于等于40岁，满足一下任意一条：\n1.胃癌高发地区人群\n2.幽门螺旋杆菌感染感染\n3.既往患有慢性萎缩性胃炎等胃癌癌前病变\n4.一级亲属罹患胃癌\n5.存在胃癌其它高危因素（吸烟、重度饮酒、高盐、腌制饮食等）',NULL,' ',NULL,1,NULL,NULL,'e613c52489824f9c8c51bf55d2b8cddb','中国早期胃癌筛查及内镜诊治共识意见（2014年，长沙）');
insert  into `exam_examguideline`(`EXAMGUIDELINE_ID`,`ORIGINATE`,`DESCRIPTION`,`CONCERNEDFACTORS`,`HIGHRISKDEFINE`,`HIGHRISKEXPRESSION`,`LOWRISKDEFINE`,`LOWRISKEXPRESSION`,`STATUS`,`CREATEBY`,`CREATEON`,`DISEASE_ID`,`NAME`) values ('e00f9b2d6bee4dc7aeb0d821f8ac068d','中华医学会消化内镜学分会消化系早癌内镜诊断与治疗协作组,中华医学会消化病学分会消化道肿瘤协作组,中华医学会消化病学分会消化病理学组',NULL,'将55～74岁的一般风险人群、40～74岁的高风险人群以及家族史不详的人群作为内镜筛查的目标人群。\n①长期居住于食管鳞癌高发区；②一级亲属有食管鳞癌病史；③既往有食管病变史(食管上皮内瘤变)；④本人有癌症史；⑤长期吸烟史；⑥长期饮酒史；⑦有不良饮食习惯如进食快、热烫饮食、高盐饮食、进食腌菜者。','有以上任意1条者视为高危人群',NULL,'一般风险人群：无上述任意1条者。',NULL,5,NULL,NULL,'bb1a5c5baed54286ad3d097ad0bc8622','中国早期食管鳞状细胞癌及癌前病变筛查与诊治共识（2015年，北京）');
